mapper.select:

select
	post_id, userId, title, content, hit, created_at
from(
	select
		/*+INDEX_DESC(free_post pk_board) */
		rownum rn, post_id, userId, title, content, hit, created_at
	from
		free_post
	where
		rownum <= #{pageNum} * #{amount}
	)
where
	rn > (#{pageNum}-1) * #{amount}

mapper.update:
update
	free_post
set 
	hit = #{hit}
where
	post_id = #{post_id}
